package day23_April6;

public class NestedLoopTask {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		// TASK 2 : 
				/*
				 * print from 15 - 5 in one line 
				 * repeat this 4 times 
				 * 
				 * */
		
		
		for( int y = 0; y < 4 ; y++) {
		//System.out.println(y +1); //to show clearly how it is work
		
		for (int x= 15 ; x>=5 ;x--) {
			System.out.print(x+ " ");
		
		}
		System.out.println();
		}
		
		
	
		
}
}
